import { CurrencyIcon, Counter } from '@ya.praktikum/react-developer-burger-ui-components';
import styles from './Ingredient.module.css';
import React from 'react';
import PropTypes from 'prop-types'; 

function Ingredients (props) {
const  [counter, setCounter] = React.useState({quantity: 0, check: false});

const modal = (
  <Modal onClose={handleCloseModal}>
    <p style={{marginTop: '80px'}} className="text text_type_digits-large">034536</p>
    <p style={{marginTop: '32px'}} className="text text_type_main-medium">идентификатор заказа</p>
    <img style={{marginTop: '60px'}} src={ConfirmationPicture} alt='Подтверждающая фотография'></img>
    <p style={{marginTop: '60px'}} className="text text_type_main-default">Ваш заказ начали готовить</p>
    <p style={{margin: '8px 0 60px 0'}}className="text text_type_main-default text_color_inactive">Дождитесь готовности на орбитальной станции</p>
  </Modal>
)

  return (
    <div className={styles.ingredient} key={props.ingredient._id}>
      {/*счетчик */}
      {counter.check &&
        <div className={styles.counter}>
          <Counter count={counter.quantity} size="default" extraClass="m-1" />
        </div>
      }
      
      <img className={styles.ingredientImage} src={props.ingredient.image} alt='каринка'></img>
      <div className={styles.price}>
        <p className="text text_type_digits-default">{props.ingredient.price}</p>
        <CurrencyIcon type="primary" />
      </div>
      <p className={styles.ingredientName}>{props.ingredient.name}</p>
    </div>
  )
}

Ingredients.propTypes = {
  api: PropTypes.array
}; 

export default Ingredients;
