import { ConstructorElement, DragIcon, Button, CurrencyIcon } from '@ya.praktikum/react-developer-burger-ui-components';
import styles from './burgerConstructor.module.css';
import React from 'react'; 
import PropTypes from 'prop-types';

function BurgerConstructor (props) {
  const [state, setState] = React.useState({ visible: false})

  const handleOpenModal = () => {
    setState({visible: true})
  }

  const handleCloseModal = () => {
    setState({visible: false})
  }


  const modal = (
    <modal onClose={handleCloseModal}>
          <p style={{marginTop: '80px'}} className="text text_type_digits-large">034536</p>
          <p style={{marginTop: '32px'}} className="text text_type_main-medium">идентификатор заказа</p>
          <img style={{marginTop: '60px'}} src={ConfirmationPicture} alt='Подтверждающая фотография'></img>
          <p style={{marginTop: '60px'}} className="text text_type_main-default">Ваш заказ начали готовить</p>
          <p style={{margin: '8px 0 60px 0'}}className="text text_type_main-default text_color_inactive">Дождитесь готовности на орбитальной станции</p>
    </modal>
  )
  
  return (
    <section className={styles.burgerConstructor}>
      <div className={styles.firstBun}>
        <ConstructorElement
          type="top"
          isLocked={true}
          text="Краторная булка N-200i"
          price={1255}
          thumbnail={"https://code.s3.yandex.net/react/code/bun-02.png"}
        />
      </div>
      <ul className={`${styles.list} custom-scroll`}>
        {props.api.map((items) => (
          <li className={styles.listItem} key={items._id}>
            <DragIcon type="primary" />
            <ConstructorElement
              key={items.id}
              text={items.name}
              price={items.price}
              thumbnail={items.image}
            />
          </li>
        ))}
      </ul>
      <div className={styles.secondBun}>
        <ConstructorElement
          type="bottom"
          isLocked={true}
          text="Краторная булка N-200i"
          price={1255}
          thumbnail={"https://code.s3.yandex.net/react/code/bun-02.png"}
        />
      </div>
      {/*Заказ*/}
      <div className={styles.orderBox}> 
        <Button htmlType="button" type="primary" size="large" onClick={handleOpenModal}>
          Оформить заказ
        </Button>
        <div style={{display: 'flex', gap: '8px', alignItems: 'center'}}>
        <p className="text text_type_digits-medium">610</p>
          <CurrencyIcon type="primary" />
        </div>
      </div>

      {/*popup*/}
      {state.visible && modal}
    </section>
    
  )
}

BurgerConstructor.propTypes = {
  api: PropTypes.array
}; 

export default BurgerConstructor;
