import { CurrencyIcon, Counter } from '@ya.praktikum/react-developer-burger-ui-components';
import styles from './Ingredient.module.css';
import React from 'react';
import PropTypes from 'prop-types'; 
import Modal from '../../Modal/Modal';

function Ingredients (props) {
const  [counter, setCounter] = React.useState({quantity: 0, check: false});
const [state, setState] = React.useState({visible: false})

const handleOpenModal = () => {
  setState({visible: true})
}
const handleCloseModal = () => {
  setState({visible: false})
}

const modal = (
  <Modal onClose={handleCloseModal}>
    <p className={`text text_type_main-large ${styles.popupHeading}`}>Детали ингредиента</p>
    <img className={styles.popupImage} src={props.ingredient.image} alt='Ошибка'></img>
    <p className={`$text text_type_main-medium ${styles.popupName}`}>{props.ingredient.name}</p>
    <ul className={styles.PopupList}>
      <li className={styles.listItem}>
      <p className="text text_type_main-default text_color_inactive">Калории,ккал</p>
      <p className="text text_type_main-default text_color_inactive">{props.ingredient.calories}</p>
      </li>
      <li className={styles.listItem}>
      <p className="text text_type_main-default text_color_inactive">Белки, г</p>
      <p className="text text_type_main-default text_color_inactive">{props.ingredient.proteins}</p>
      </li>
      <li className={styles.listItem}>
      <p className="text text_type_main-default text_color_inactive">Жиры, г</p>
      <p className="text text_type_main-default text_color_inactive">{props.ingredient.fat}</p>
      </li>
      <li className={styles.listItem}>
      <p className="text text_type_main-default text_color_inactive">Углеводы, г</p>
      <p className="text text_type_main-default text_color_inactive">{props.ingredient.carbohydrates}</p>
      </li>
    </ul>
  </Modal>
)

  return (
      <div className={styles.ingredient} onClick={handleOpenModal} key={props.ingredient._id}>
        {/*счетчик */}
        {counter.check &&
          <div className={styles.counter}>
            <Counter count={counter.quantity} size="default" extraClass="m-1" />
          </div>
        }
        
        <img className={styles.ingredientImage} src={props.ingredient.image} alt='каринка'></img>
        <div className={styles.price}>
          <p className="text text_type_digits-default">{props.ingredient.price}</p>
          <CurrencyIcon type="primary" />
        </div>
        <p className={styles.ingredientName}>{props.ingredient.name}</p>
        {state.visible && modal};
      </div>
  )
}

Ingredients.propTypes = {
  api: PropTypes.array
}; 

export default Ingredients;
