import PropTypes from "prop-types";

import React from 'react';
import ReactDOM from 'react-dom';
import styles from './prop-types.module.css';
import { CloseIcon } from '@ya.praktikum/react-developer-burger-ui-components';

export const ingredientPropType = PropTypes.shape({
  //укажите здесь prop-types для ингридиента
});








const modalRoot = document.getElementById("react-modals");



export function Modal (props) {

  const modalOverlay = React.useRef(null);
  const closeButton = React.useRef(null);
  
  const close = (e) => {
    if(e.target === modalOverlay.current || e.target === <CloseIcon type="primary" />){
      props.onClose()
    }
  }

    return ReactDOM.createPortal((
      <div className={styles.modalOverlay} ref={modalOverlay} onClick={close}>
        <div className={styles.popup}>
          <button className={styles.closeButton} onClick={close}><CloseIcon type="primary" /></button>
          {props.children}
        </div>
      </div>
    ), modalRoot);
  }
